mapper.select:
SELECT SQL_CALC_FOUND_ROWS

FROM
   tbl_sdk_ios AS a, tbl_sdk_version_ios AS ver, tbl_user AS us
WHERE
    a.status = #{FLAG_STATUS_ACTIVE}
    AND ver.isNew = #{FLAG_STATUS_ACTIVE}
    AND a.sdkId = ver.sdkId
    AND ver.userId = us.userId

    AND a.sdkId = #{sdkId}

ORDER BY
    a.sdkCchId ASC
LIMIT #{pageStart}, #{rows}

SELECT

FROM tbl_sdk_ios
WHERE status = #{FLAG_STATUS_ACTIVE}
AND sdkName = #{sdkName}

SELECT

FROM tbl_sdk_ios
WHERE status = #{FLAG_STATUS_ACTIVE}
AND sdkCchId = #{sdkCchId}
